for i in `dir results/`; do groups_array=`wc -l "results/"$i"/"$i"_parsed.txt.duplicate_relations.txt"`; groups=($(echo "$groups_array" | tr '\t' '\n')); dups=`grep -c '>' results/$i/"$i"_BLAST.out.allseqs_duplicated.fasta`; all_seqs=`grep -c '>' results/$i/"$i"_clean.fasta`; transpo=`grep -c 'transposase' "results/"$i"/"$i"_BLAST.out.results.csv"`; hypo=`grep -c 'hypothetical' "results/"$i"/"$i"_BLAST.out.results.csv"`; DUF=`grep -c 'DUF' "results/"$i"/"$i"_BLAST.out.results.csv"`; echo $i,$groups,$dups,$all_seqs,$transpo,$hypo,$DUF; done 
